import React, { useState, useEffect } from 'react';
import Icon from '../Icon';
import TimeSlider from '../TimeSlider';
import './NetworkModal.css';

const NetworkModal = ({ isOpen, onClose, onSave }) => {
  const [referringMethod, setReferringMethod] = useState('waterfall');
  const [timeValue, setTimeValue] = useState(2);
  const [timeUnit, setTimeUnit] = useState('hours');
  const [marketplaceValue, setMarketplaceValue] = useState(24);
  const [marketplaceUnit, setMarketplaceUnit] = useState('hours');

  // Convert values to hours for slider calculations
  const getTimeInHours = (value, unit) => {
    return unit === 'days' ? Math.min(value * 24, 72) : value;
  };

  const getMarketplaceInHours = (value, unit) => {
    return unit === 'days' ? Math.min(value * 24, 168) : value;
  };

  const handleTimeSliderChange = (hours) => {
    if (timeUnit === 'days') {
      setTimeValue(Math.round(hours / 24));
    } else {
      setTimeValue(hours);
    }
  };

  const handleMarketplaceSliderChange = (hours) => {
    if (marketplaceUnit === 'days') {
      setMarketplaceValue(Math.round(hours / 24));
    } else {
      setMarketplaceValue(hours);
    }
  };

  const handleTimeUnitChange = (unit) => {
    const currentHours = getTimeInHours(timeValue, timeUnit);
    setTimeUnit(unit);
    if (unit === 'days') {
      setTimeValue(Math.round(currentHours / 24));
    } else {
      setTimeValue(currentHours);
    }
  };

  const handleMarketplaceUnitChange = (unit) => {
    const currentHours = getMarketplaceInHours(marketplaceValue, marketplaceUnit);
    setMarketplaceUnit(unit);
    if (unit === 'days') {
      setMarketplaceValue(Math.round(currentHours / 24));
    } else {
      setMarketplaceValue(currentHours);
    }
  };

  const handleSave = () => {
    const settings = {
      referringMethod,
      timePerAttorney: `${timeValue} ${timeUnit}`,
      marketplaceFallback: `${marketplaceValue} ${marketplaceUnit}`
    };

    onSave(settings);
    onClose();
  };

  const handleOverlayClick = (e) => {
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  // Prevent body scroll when modal is open
  useEffect(() => {
    if (isOpen) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = '';
    }

    return () => {
      document.body.style.overflow = '';
    };
  }, [isOpen]);

  if (!isOpen) return null;

  const timeSliderValue = getTimeInHours(timeValue, timeUnit);
  const marketplaceSliderValue = getMarketplaceInHours(marketplaceValue, marketplaceUnit);

  return (
    <div className="modal-overlay" onClick={handleOverlayClick}>
      <div className="network-modal">
        <div className="network-modal-header">
          <h2>Referral Network Settings</h2>
          <button className="modal-close-btn" onClick={onClose} aria-label="Close">
            <Icon name="close" />
          </button>
        </div>

        <div className="network-modal-content">
          {/* Referring Method Section */}
          <div className="settings-section">
            <div className="section-header">
              <Icon name="layers" className="section-icon" />
              <h3>Referring Method</h3>
            </div>
            <p className="section-description">How referrals will be sent to your network</p>

            <div className="referring-options">
              <label className={`radio-card ${referringMethod === 'waterfall' ? 'selected' : ''}`}>
                <input
                  type="radio"
                  name="referring-method"
                  value="waterfall"
                  checked={referringMethod === 'waterfall'}
                  onChange={(e) => setReferringMethod(e.target.value)}
                />
                <div className="radio-card-content">
                  <div className="radio-card-header">
                    <div className="custom-radio"></div>
                    <div>
                      <h4>Waterfall Referrals™</h4>
                      <span className="badge">One at a time</span>
                    </div>
                  </div>
                  <p className="radio-card-description">
                    Send referrals to the best fit attorney, then cascade to the next one if not accepted. Referrals will be distributed evenly to qualifying attorneys in your network.
                  </p>
                </div>
              </label>

              <label className="radio-card disabled">
                <input type="radio" name="referring-method" value="broadcast" disabled />
                <div className="radio-card-content">
                  <div className="radio-card-header">
                    <div className="custom-radio"></div>
                    <div>
                      <h4>Broadcast</h4>
                      <span className="badge coming-soon">Coming Soon</span>
                      <span className="badge secondary">Multiple at once</span>
                    </div>
                  </div>
                  <p className="radio-card-description">
                    Send referrals to 5 attorneys simultaneously, then cascade to the next bunch if not accepted. The first attorney to confirm receives client information.
                  </p>
                </div>
              </label>
            </div>
          </div>

          {/* Time per Attorney Section */}
          <div className="settings-section">
            <div className="section-header">
              <svg className="section-icon" width="24" height="24" viewBox="0 0 24 24" fill="none">
                <circle cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="2"/>
                <path d="M12 6V12L16 14" stroke="currentColor" strokeWidth="2"/>
              </svg>
              <h3>Time per Attorney</h3>
            </div>
            <p className="section-description">How long each attorney has before the case moves to the next in the queue</p>

            <div className="slider-container">
              <input
                type="range"
                className="custom-slider"
                min="1"
                max="72"
                value={timeSliderValue}
                onChange={(e) => handleTimeSliderChange(parseInt(e.target.value))}
              />
              <div className="slider-track">
                <div
                  className="slider-fill"
                  style={{ width: `${((timeSliderValue - 1) / 71) * 100}%` }}
                ></div>
              </div>
              <div className="slider-value">
                <input
                  type="number"
                  value={timeValue}
                  min="1"
                  max={timeUnit === 'days' ? "3" : "72"}
                  onChange={(e) => setTimeValue(parseInt(e.target.value) || 1)}
                />
                <select value={timeUnit} onChange={(e) => handleTimeUnitChange(e.target.value)}>
                  <option value="hours">hours</option>
                  <option value="days">days</option>
                </select>
              </div>
            </div>
          </div>

          {/* Marketplace Fallback Section */}
          <div className="settings-section">
            <div className="section-header">
              <svg className="section-icon" width="24" height="24" viewBox="0 0 24 24" fill="none">
                <path d="M3 9L12 2L21 9V20C21 20.5304 20.7893 21.0391 20.4142 21.4142C20.0391 21.7893 19.5304 22 19 22H5C4.46957 22 3.96086 21.7893 3.58579 21.4142C3.21071 21.0391 3 20.5304 3 20V9Z" stroke="currentColor" strokeWidth="2"/>
                <path d="M9 22V12H15V22" stroke="currentColor" strokeWidth="2"/>
              </svg>
              <h3>Marketplace Fallback</h3>
            </div>
            <p className="section-description">Send referrals to the marketplace if no one accepts within the specified time.</p>

            <div className="slider-container">
              <input
                type="range"
                className="custom-slider"
                min="1"
                max="168"
                value={marketplaceSliderValue}
                onChange={(e) => handleMarketplaceSliderChange(parseInt(e.target.value))}
              />
              <div className="slider-track">
                <div
                  className="slider-fill"
                  style={{ width: `${((marketplaceSliderValue - 1) / 167) * 100}%` }}
                ></div>
              </div>
              <div className="slider-value">
                <input
                  type="number"
                  value={marketplaceValue}
                  min="1"
                  max={marketplaceUnit === 'days' ? "7" : "168"}
                  onChange={(e) => setMarketplaceValue(parseInt(e.target.value) || 1)}
                />
                <select value={marketplaceUnit} onChange={(e) => handleMarketplaceUnitChange(e.target.value)}>
                  <option value="hours">hours</option>
                  <option value="days">days</option>
                </select>
              </div>
            </div>
          </div>
        </div>

        <div className="network-modal-footer">
          <button className="btn-cancel" onClick={onClose}>Cancel</button>
          <button className="btn-save" onClick={handleSave}>Save Settings</button>
        </div>
      </div>
    </div>
  );
};

export default NetworkModal;
