import React from 'react';

const Icon = ({ name, size = 24, className = '', ...props }) => {
  const [svgContent, setSvgContent] = React.useState(null);
  const [error, setError] = React.useState(false);

  React.useEffect(() => {
    const loadSvg = async () => {
      try {
        // Import the SVG file dynamically
        const svgModule = await import(`../../assets/icons/${name}.svg`);
        setSvgContent(svgModule.default);
        setError(false);
      } catch (err) {
        console.warn(`Icon "${name}" not found in assets/icons/ folder`);
        setError(true);
        setSvgContent(null);
      }
    };

    loadSvg();
  }, [name]);

  if (error) {
    // Fallback: render a simple placeholder
    return (
      <div 
        className={`icon-wrapper icon-placeholder ${className}`} 
        style={{
          width: size,
          height: size,
          backgroundColor: 'currentColor',
          opacity: 0.3,
          borderRadius: '2px',
          display: 'inline-block'
        }}
        {...props}
      />
    );
  }

  if (!svgContent) {
    // Loading state
    return (
      <div 
        className={`icon-wrapper icon-loading ${className}`} 
        style={{
          width: size,
          height: size,
          backgroundColor: 'currentColor',
          opacity: 0.1,
          borderRadius: '2px',
          display: 'inline-block'
        }}
        {...props}
      />
    );
  }

  const SvgComponent = svgContent;

  return (
    <div className={`icon-wrapper ${className}`} {...props}>
      <SvgComponent 
        width={size} 
        height={size}
        style={{ 
          display: 'block',
          fill: 'currentColor'
        }}
      />
    </div>
  );
};

export default Icon;
