import React, { useState, useEffect } from 'react';
import Icon from '../Icon';
import './AddToNetworkModal.css';

const caseTypes = [
  'Personal Injury',
  'Car Accidents',
  'Medical Malpractice',
  'Slip & Fall',
  'Workers Compensation',
  'Criminal Defense',
  'DUI',
  'Family Law',
  'Divorce',
  'Employment Law',
  'Business Law',
  'Real Estate',
  'Immigration',
  'Tax Law',
  'Estate Planning'
];

const AddToNetworkModal = ({ isOpen, onClose, attorney, onAdd }) => {
  const [feePercentage, setFeePercentage] = useState(25);
  const [selectedCaseTypes, setSelectedCaseTypes] = useState([]);
  const [notes, setNotes] = useState('');
  const [preferredContact, setPreferredContact] = useState('email');

  // Initialize with attorney's specialties
  useEffect(() => {
    if (isOpen && attorney) {
      setSelectedCaseTypes(attorney.specialties || []);
      setFeePercentage(25);
      setNotes('');
      setPreferredContact('email');
    }
  }, [isOpen, attorney]);

  const handleOverlayClick = (e) => {
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  const toggleCaseType = (caseType) => {
    setSelectedCaseTypes(prev => {
      if (prev.includes(caseType)) {
        return prev.filter(ct => ct !== caseType);
      } else {
        return [...prev, caseType];
      }
    });
  };

  const handleAdd = () => {
    const networkSettings = {
      attorney: attorney,
      feePercentage,
      selectedCaseTypes,
      notes,
      preferredContact,
      addedDate: new Date().toISOString()
    };

    onAdd(networkSettings);
    onClose();
  };

  // Prevent body scroll when modal is open
  useEffect(() => {
    if (isOpen) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = '';
    }

    return () => {
      document.body.style.overflow = '';
    };
  }, [isOpen]);

  if (!isOpen || !attorney) return null;

  return (
    <div className="add-to-network-modal-overlay" onClick={handleOverlayClick}>
      <div className="add-to-network-modal">
        <div className="modal-header">
          <h2>Add to My Network</h2>
          <button className="modal-close-btn" onClick={onClose} aria-label="Close">
            <Icon name="close" />
          </button>
        </div>

        {/* Attorney Details Section */}
        <div className="attorney-details-section">
          <div className="attorney-profile">
            <div className="attorney-avatar-large">
              <img 
                src={`https://ui-avatars.com/api/?name=${attorney.initials}&background=002e69&color=fff&size=80`}
                alt={attorney.name}
              />
            </div>
            <div className="attorney-info-main">
              <h3>{attorney.name}</h3>
              <p className="attorney-firm-text">{attorney.firm}</p>
              <p className="attorney-location-text">
                <Icon name="location" />
                {attorney.location}
              </p>
              <div className="attorney-specialties-list">
                {attorney.specialties.map((specialty, index) => (
                  <span key={index} className="specialty-badge">
                    {specialty}
                  </span>
                ))}
              </div>
            </div>
          </div>

          {/* Stats Preview */}
          <div className="attorney-stats-preview">
            <div className="stat-preview-item">
              <span className="stat-icon">‚≠ê</span>
              <div className="stat-content">
                <span className="stat-value">4.8</span>
                <span className="stat-label">Rating</span>
              </div>
            </div>
            <div className="stat-preview-item">
              <span className="stat-icon">üìä</span>
              <div className="stat-content">
                <span className="stat-value">156</span>
                <span className="stat-label">Cases</span>
              </div>
            </div>
            <div className="stat-preview-item">
              <span className="stat-icon">‚è±Ô∏è</span>
              <div className="stat-content">
                <span className="stat-value">2.5h</span>
                <span className="stat-label">Response</span>
              </div>
            </div>
          </div>
        </div>

        {/* Configuration Section */}
        <div className="configuration-section">
          {/* Fee Percentage */}
          <div className="config-group">
            <label className="config-label">
              <Icon name="percent" />
              Referral Fee Percentage
            </label>
            <div className="fee-slider-container">
              <input
                type="range"
                min="10"
                max="40"
                value={feePercentage}
                onChange={(e) => setFeePercentage(parseInt(e.target.value))}
                className="fee-slider"
              />
              <div className="fee-display">
                <input
                  type="number"
                  min="10"
                  max="40"
                  value={feePercentage}
                  onChange={(e) => setFeePercentage(parseInt(e.target.value) || 25)}
                  className="fee-input"
                />
                <span className="fee-percent">%</span>
              </div>
            </div>
            <div className="fee-hints">
              <span className={feePercentage <= 20 ? 'active' : ''}>Low</span>
              <span className={feePercentage > 20 && feePercentage <= 30 ? 'active' : ''}>Standard</span>
              <span className={feePercentage > 30 ? 'active' : ''}>High</span>
            </div>
          </div>

          {/* Case Types */}
          <div className="config-group">
            <label className="config-label">
              <Icon name="briefcase-search" />
              Types of Cases to Refer
            </label>
            <div className="case-types-grid">
              {caseTypes.map((caseType) => (
                <label 
                  key={caseType} 
                  className={`case-type-checkbox ${selectedCaseTypes.includes(caseType) ? 'selected' : ''}`}
                >
                  <input
                    type="checkbox"
                    checked={selectedCaseTypes.includes(caseType)}
                    onChange={() => toggleCaseType(caseType)}
                  />
                  <span className="checkbox-custom"></span>
                  <span className="case-type-label">{caseType}</span>
                </label>
              ))}
            </div>
          </div>

          {/* Preferred Contact Method */}
          <div className="config-group">
            <label className="config-label">
              <Icon name="chat" />
              Preferred Contact Method
            </label>
            <div className="contact-options">
              <label className={`contact-option ${preferredContact === 'email' ? 'selected' : ''}`}>
                <input
                  type="radio"
                  name="contact"
                  value="email"
                  checked={preferredContact === 'email'}
                  onChange={(e) => setPreferredContact(e.target.value)}
                />
                <Icon name="mail" />
                <span>Email</span>
              </label>
              <label className={`contact-option ${preferredContact === 'phone' ? 'selected' : ''}`}>
                <input
                  type="radio"
                  name="contact"
                  value="phone"
                  checked={preferredContact === 'phone'}
                  onChange={(e) => setPreferredContact(e.target.value)}
                />
                <Icon name="call" />
                <span>Phone</span>
              </label>
              <label className={`contact-option ${preferredContact === 'platform' ? 'selected' : ''}`}>
                <input
                  type="radio"
                  name="contact"
                  value="platform"
                  checked={preferredContact === 'platform'}
                  onChange={(e) => setPreferredContact(e.target.value)}
                />
                <Icon name="chat" />
                <span>Platform Message</span>
              </label>
            </div>
          </div>

          {/* Notes */}
          <div className="config-group">
            <label className="config-label">
              <Icon name="document" />
              Notes (Optional)
            </label>
            <textarea
              className="notes-textarea"
              placeholder="Add any special instructions or notes about this attorney..."
              value={notes}
              onChange={(e) => setNotes(e.target.value)}
              rows="3"
            />
          </div>
        </div>

        {/* Footer */}
        <div className="modal-footer">
          <div className="footer-info">
            <Icon name="info" />
            <span>You can modify these settings anytime from your Network Members list</span>
          </div>
          <div className="footer-buttons">
            <button className="btn-cancel" onClick={onClose}>Cancel</button>
            <button 
              className="btn-add-to-network" 
              onClick={handleAdd}
              disabled={selectedCaseTypes.length === 0}
            >
              Add to Network
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddToNetworkModal;
