import React, { useState, useEffect } from 'react';
import Icon from '../Icon';
import LocationMultiselect from '../LocationMultiselect';
import './FiltersModal.css';

const practiceAreas = [
  'Personal Injury',
  'Car Accidents',
  'Medical Malpractice',
  'Slip & Fall',
  'Workers Compensation',
  'Criminal Defense',
  'DUI',
  'Family Law',
  'Divorce',
  'Child Custody',
  'Employment Law',
  'Business Law',
  'Real Estate',
  'Immigration',
  'Tax Law',
  'Estate Planning',
  'Bankruptcy',
  'Intellectual Property'
];

const experienceLevels = [
  { value: '1-5', label: '1-5 years' },
  { value: '5-10', label: '5-10 years' },
  { value: '10-15', label: '10-15 years' },
  { value: '15+', label: '15+ years' }
];

const FiltersModal = ({ isOpen, onClose, onApply, currentFilters = {} }) => {
  const [filters, setFilters] = useState({
    locations: [],
    practiceAreas: [],
    experienceLevel: [],
    rating: '',
    caseLoad: '',
    responseTime: ''
  });

  // Initialize filters when modal opens
  useEffect(() => {
    if (isOpen) {
      setFilters({
        locations: currentFilters.locations || [],
        practiceAreas: currentFilters.practiceAreas || [],
        experienceLevel: currentFilters.experienceLevel || [],
        rating: currentFilters.rating || '',
        caseLoad: currentFilters.caseLoad || '',
        responseTime: currentFilters.responseTime || ''
      });
    }
  }, [isOpen, currentFilters]);

  // Prevent body scroll when modal is open
  useEffect(() => {
    if (isOpen) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = '';
    }

    return () => {
      document.body.style.overflow = '';
    };
  }, [isOpen]);

  const handleOverlayClick = (e) => {
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  const handlePracticeAreaToggle = (area) => {
    setFilters(prev => ({
      ...prev,
      practiceAreas: prev.practiceAreas.includes(area)
        ? prev.practiceAreas.filter(pa => pa !== area)
        : [...prev.practiceAreas, area]
    }));
  };

  const handleExperienceToggle = (level) => {
    setFilters(prev => ({
      ...prev,
      experienceLevel: prev.experienceLevel.includes(level)
        ? prev.experienceLevel.filter(el => el !== level)
        : [...prev.experienceLevel, level]
    }));
  };

  const handleClearAll = () => {
    setFilters({
      locations: [],
      practiceAreas: [],
      experienceLevel: [],
      rating: '',
      caseLoad: '',
      responseTime: ''
    });
  };

  const handleApply = () => {
    onApply(filters);
    onClose();
  };

  const hasActiveFilters = () => {
    return filters.locations.length > 0 ||
           filters.practiceAreas.length > 0 ||
           filters.experienceLevel.length > 0 ||
           filters.rating ||
           filters.caseLoad ||
           filters.responseTime;
  };

  if (!isOpen) return null;

  return (
    <div className="filters-modal-overlay" onClick={handleOverlayClick}>
      <div className="filters-modal">
        <div className="modal-header">
          <h2>Filter Attorneys</h2>
          <button className="modal-close-btn" onClick={onClose} aria-label="Close">
            <Icon name="close" />
          </button>
        </div>

        <div className="modal-body">
          {/* Location Filter */}
          <div className="filter-group">
            <label className="filter-label">
              <Icon name="location" />
              Location
            </label>
            <LocationMultiselect
              selectedLocations={filters.locations}
              onChange={(locations) => setFilters(prev => ({ ...prev, locations }))}
              placeholder="Select locations..."
            />
          </div>

          {/* Practice Areas Filter */}
          <div className="filter-group">
            <label className="filter-label">
              <Icon name="briefcase-search" />
              Practice Areas
            </label>
            <div className="checkbox-grid">
              {practiceAreas.map((area) => (
                <label key={area} className={`checkbox-item ${filters.practiceAreas.includes(area) ? 'selected' : ''}`}>
                  <input
                    type="checkbox"
                    checked={filters.practiceAreas.includes(area)}
                    onChange={() => handlePracticeAreaToggle(area)}
                  />
                  <span className="checkbox-custom"></span>
                  <span className="checkbox-label">{area}</span>
                </label>
              ))}
            </div>
          </div>

          {/* Experience Level Filter */}
          <div className="filter-group">
            <label className="filter-label">
              <Icon name="calendar" />
              Years of Experience
            </label>
            <div className="checkbox-row">
              {experienceLevels.map((level) => (
                <label key={level.value} className={`checkbox-item ${filters.experienceLevel.includes(level.value) ? 'selected' : ''}`}>
                  <input
                    type="checkbox"
                    checked={filters.experienceLevel.includes(level.value)}
                    onChange={() => handleExperienceToggle(level.value)}
                  />
                  <span className="checkbox-custom"></span>
                  <span className="checkbox-label">{level.label}</span>
                </label>
              ))}
            </div>
          </div>

          {/* Rating Filter */}
          <div className="filter-group">
            <label className="filter-label">
              <Icon name="star" />
              Minimum Rating
            </label>
            <div className="rating-options">
              {[5, 4, 3, 2, 1].map((rating) => (
                <label key={rating} className={`rating-option ${filters.rating === rating.toString() ? 'selected' : ''}`}>
                  <input
                    type="radio"
                    name="rating"
                    value={rating}
                    checked={filters.rating === rating.toString()}
                    onChange={(e) => setFilters(prev => ({ ...prev, rating: e.target.value }))}
                  />
                  <div className="stars">
                    {Array.from({ length: 5 }, (_, i) => (
                      <Icon key={i} name="star" className={i < rating ? 'filled' : 'empty'} />
                    ))}
                  </div>
                  <span>{rating}.0 & above</span>
                </label>
              ))}
            </div>
          </div>

          {/* Case Load Filter */}
          <div className="filter-group">
            <label className="filter-label">
              <Icon name="briefcase" />
              Current Case Load
            </label>
            <div className="radio-group">
              {[
                { value: 'light', label: 'Light (Available immediately)' },
                { value: 'moderate', label: 'Moderate (Available within 1 week)' },
                { value: 'heavy', label: 'Heavy (Available within 1 month)' }
              ].map((option) => (
                <label key={option.value} className={`radio-item ${filters.caseLoad === option.value ? 'selected' : ''}`}>
                  <input
                    type="radio"
                    name="caseLoad"
                    value={option.value}
                    checked={filters.caseLoad === option.value}
                    onChange={(e) => setFilters(prev => ({ ...prev, caseLoad: e.target.value }))}
                  />
                  <span className="radio-custom"></span>
                  <span className="radio-label">{option.label}</span>
                </label>
              ))}
            </div>
          </div>

          {/* Response Time Filter */}
          <div className="filter-group">
            <label className="filter-label">
              <Icon name="clock" />
              Maximum Response Time
            </label>
            <div className="radio-group">
              {[
                { value: '1h', label: 'Within 1 hour' },
                { value: '4h', label: 'Within 4 hours' },
                { value: '24h', label: 'Within 24 hours' },
                { value: '3d', label: 'Within 3 days' }
              ].map((option) => (
                <label key={option.value} className={`radio-item ${filters.responseTime === option.value ? 'selected' : ''}`}>
                  <input
                    type="radio"
                    name="responseTime"
                    value={option.value}
                    checked={filters.responseTime === option.value}
                    onChange={(e) => setFilters(prev => ({ ...prev, responseTime: e.target.value }))}
                  />
                  <span className="radio-custom"></span>
                  <span className="radio-label">{option.label}</span>
                </label>
              ))}
            </div>
          </div>
        </div>

        <div className="modal-footer">
          <div className="footer-actions">
            <button 
              className="btn-clear" 
              onClick={handleClearAll}
              disabled={!hasActiveFilters()}
            >
              Clear All
            </button>
            <div className="primary-actions">
              <button className="btn-cancel" onClick={onClose}>
                Cancel
              </button>
              <button className="btn-apply" onClick={handleApply}>
                Apply Filters
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default FiltersModal;
