import React, { useState } from 'react';
import Icon from '../Icon';
import './AttorneyCarousel.css';

const AttorneyCard = ({ attorney }) => {
  return (
    <div className="attorney-card">
      <div className="attorney-avatar">
        <img
          src={`https://ui-avatars.com/api/?name=${attorney.initials}&background=random`}
          alt={attorney.name}
        />
      </div>
      <div className="attorney-info">
        <h4 className="attorney-name">{attorney.name}</h4>
        <p className="attorney-firm">{attorney.firm}</p>
        <p className="attorney-location">{attorney.location}</p>
        <div className="attorney-specialties">
          {attorney.specialties.map((specialty, index) => (
            <span key={index} className="specialty-tag">
              {specialty}
            </span>
          ))}
        </div>
      </div>
      <div className="attorney-actions">
        <button className="btn-add">Add to Network</button>
      </div>
    </div>
  );
};

const AttorneyCarousel = ({ attorneys, categoryId, title }) => {
  const [currentPosition, setCurrentPosition] = useState(0);
  const cardsPerView = 2;
  const totalCards = attorneys.length;
  const maxPosition = Math.max(0, totalCards - cardsPerView);

  const navigateNext = () => {
    if (currentPosition < maxPosition) {
      setCurrentPosition(currentPosition + 1);
    }
  };

  const navigatePrev = () => {
    if (currentPosition > 0) {
      setCurrentPosition(currentPosition - 1);
    }
  };

  const translateX = -(currentPosition * (100 / cardsPerView));

  return (
    <section className="attorney-section">
      <h3 className="section-title">{title}</h3>
      <div className="carousel-container">
        <button
          className={`carousel-btn carousel-prev ${currentPosition === 0 ? 'disabled' : ''}`}
          onClick={navigatePrev}
          disabled={currentPosition === 0}
          aria-label="Previous"
        >
          <Icon name="chevron-left" />
        </button>

        <div className="attorney-carousel">
          <div
            className="attorney-carousel-track"
            style={{ transform: `translateX(${translateX}%)` }}
          >
            {attorneys.map((attorney, index) => (
              <AttorneyCard key={index} attorney={attorney} />
            ))}
          </div>
        </div>

        <button
          className={`carousel-btn carousel-next ${currentPosition >= maxPosition ? 'disabled' : ''}`}
          onClick={navigateNext}
          disabled={currentPosition >= maxPosition}
          aria-label="Next"
        >
          <Icon name="chevron-right" />
        </button>
      </div>
    </section>
  );
};

export default AttorneyCarousel;
